import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Card from 'react-bootstrap/Card';
import Table from 'react-bootstrap/Table';
import Badge from 'react-bootstrap/Badge';
import Button from 'react-bootstrap/Button';
import Spinner from 'react-bootstrap/Spinner';
import Alert from 'react-bootstrap/Alert';
import axios from 'axios';
import AdminHeader from '../../components/AdminHeader';
import NotificationBell from '../../components/NotificationBell';
import './AdminDashboard.css';

const formatCurrency = (value) => {
  const number = Number(value) || 0;
  return `฿${number.toLocaleString('th-TH', {
    minimumFractionDigits: 0,
    maximumFractionDigits: 0
  })}`;
};

const formatDate = (value) => {
  if (!value) {
    return '-';
  }
  const date = new Date(value);
  if (Number.isNaN(date.getTime())) {
    return '-';
  }
  return date.toLocaleDateString('th-TH', {
    year: 'numeric',
    month: 'short',
    day: 'numeric'
  });
};

const getDaysUntilDue = (dueDate) => {
  if (!dueDate) return null;
  const today = new Date();
  today.setHours(0, 0, 0, 0);
  const due = new Date(dueDate);
  due.setHours(0, 0, 0, 0);
  const diffTime = due - today;
  const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
  return diffDays;
};

export default function AdminPaymentConfirmation({
  admin,
  logout,
  isSidebarCollapsed,
  toggleSidebar,
  adminNotifications,
  markAdminOrdersSeen,
  markAdminCustomersSeen
}) {
  const navigate = useNavigate();
  const [installments, setInstallments] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [lastFetchedAt, setLastFetchedAt] = useState(null);
  const [message, setMessage] = useState({ show: false, text: '', variant: '' });

  useEffect(() => {
    fetchUpcomingPayments();
  }, []);

  const fetchUpcomingPayments = async () => {
    setIsLoading(true);
    try {
      const response = await axios.get('/api/admin/payment-confirmations');
      setInstallments(response.data);
    } catch (error) {
      console.error('Error fetching upcoming payments:', error);
      setMessage({
        show: true,
        text: error.response?.data?.error || 'ไม่สามารถโหลดข้อมูลได้',
        variant: 'danger'
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleMarkPaid = async (orderId, installmentId) => {
    if (!window.confirm('ยืนยันการบันทึกว่าชำระเงินแล้ว?')) {
      return;
    }

    try {
      await axios.put(`/api/orders/${orderId}/installments/${installmentId}/mark-paid`);
      setMessage({
        show: true,
        text: 'บันทึกสถานะการชำระเงินเรียบร้อยแล้ว',
        variant: 'success'
      });
      
      // รีเฟรชข้อมูล
      await fetchUpcomingPayments();
      
      setTimeout(() => setMessage({ show: false }), 3000);
    } catch (error) {
      console.error('Error marking installment paid:', error);
      setMessage({
        show: true,
        text: error.response?.data?.error || 'ไม่สามารถบันทึกสถานะการชำระเงินได้',
        variant: 'danger'
      });
      setTimeout(() => setMessage({ show: false }), 3000);
    }
  };

  const handleViewOrder = (orderId) => {
    navigate(`/admin/orders`);
    // เปิด order detail ในหน้า orders
    setTimeout(() => {
      const event = new CustomEvent('admin-view-order', { detail: { orderId } });
      window.dispatchEvent(event);
    }, 100);
  };

  const getUrgencyBadge = (daysUntilDue) => {
    if (daysUntilDue === null) return null;
    if (daysUntilDue < 0) {
      return <Badge bg="danger">เลยกำหนด</Badge>;
    } else if (daysUntilDue === 0) {
      return <Badge bg="warning">วันนี้</Badge>;
    } else if (daysUntilDue <= 2) {
      return <Badge bg="warning">{daysUntilDue} วัน</Badge>;
    } else {
      return <Badge bg="info">{daysUntilDue} วัน</Badge>;
    }
  };

  return (
    <div className="admin-payment-confirmation-page">
      <AdminHeader
        admin={admin}
        onLogout={logout}
        isCollapsed={isSidebarCollapsed}
        onToggleSidebar={toggleSidebar}
        adminNotifications={adminNotifications}
        markAdminOrdersSeen={markAdminOrdersSeen}
        markAdminCustomersSeen={markAdminCustomersSeen}
      />
      <div className={`admin-main-content ${isSidebarCollapsed ? 'admin-main-content--expanded' : ''}`}>
        <Container fluid className="admin-container">
          <div className="admin-page-header">
            <h1 className="admin-page-title">
              <i className="bi bi-cash-coin me-2"></i>
              ยืนยันการชำระเงิน
            </h1>
            <p className="admin-page-subtitle">
              รายการงวดการชำระเงินที่กำลังถึงวันกำหนดภายใน 5 วัน
            </p>
          </div>

          {message.show && (
            <Alert
              variant={message.variant}
              dismissible
              onClose={() => setMessage({ show: false })}
              className="mb-4"
            >
              {message.text}
            </Alert>
          )}

          {isLoading ? (
            <div className="text-center py-5">
              <Spinner animation="border" variant="primary" />
              <p className="mt-3 text-muted">กำลังโหลดข้อมูล...</p>
            </div>
          ) : installments.length === 0 ? (
            <Card>
              <Card.Body className="text-center py-5">
                <i className="bi bi-check-circle text-success" style={{ fontSize: '3rem' }}></i>
                <h4 className="mt-3">ไม่มีรายการที่ต้องยืนยัน</h4>
                <p className="text-muted">ไม่มีงวดการชำระเงินที่กำลังถึงวันกำหนดภายใน 5 วัน</p>
              </Card.Body>
            </Card>
          ) : (
            <Card>
              <Card.Body>
                <div className="d-flex justify-content-between align-items-center mb-3">
                  <h5 className="mb-0">รายการที่ต้องยืนยัน ({installments.length} รายการ)</h5>
                  <Button variant="outline-primary" size="sm" onClick={fetchUpcomingPayments}>
                    <i className="bi bi-arrow-clockwise me-1"></i>
                    รีเฟรช
                  </Button>
                </div>
                <Table responsive hover>
                  <thead>
                    <tr>
                      <th>ลำดับ</th>
                      <th>เลขที่คำสั่งซื้อ</th>
                      <th>ลูกค้า</th>
                      <th>เบอร์ติดต่อ</th>
                      <th>งวดที่</th>
                      <th>ยอดชำระ</th>
                      <th>กำหนดชำระ</th>
                      <th>เหลืออีก</th>
                      <th>จัดการ</th>
                    </tr>
                  </thead>
                  <tbody>
                    {installments.map((item, index) => {
                      const daysUntilDue = getDaysUntilDue(item.payment_due_date);
                      const customerName = `${item.customer.customer_fname || ''} ${item.customer.customer_lname || ''}`.trim() || 'ไม่ระบุชื่อ';
                      
                      return (
                        <tr key={item.installment_id}>
                          <td>{index + 1}</td>
                          <td>
                            <Button
                              variant="link"
                              className="p-0 text-decoration-none"
                              onClick={() => handleViewOrder(item.order_id)}
                            >
                              #{item.order_id}
                            </Button>
                          </td>
                          <td>{customerName}</td>
                          <td>{item.customer.customer_tel || '-'}</td>
                          <td>{item.installment_number}</td>
                          <td>{formatCurrency(item.installment_amount)}</td>
                          <td>{formatDate(item.payment_due_date)}</td>
                          <td>{getUrgencyBadge(daysUntilDue)}</td>
                          <td>
                            <Button
                              variant="success"
                              size="sm"
                              onClick={() => handleMarkPaid(item.order_id, item.installment_id)}
                            >
                              <i className="bi bi-check-circle me-1"></i>
                              ชำระเงินแล้ว
                            </Button>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </Table>
              </Card.Body>
            </Card>
          )}
        </Container>
      </div>
    </div>
  );
}
